import time
from typing import List

#二分搜索两字符串的最长前缀
class Solution:
    def findLength_dp(self, A, B):
        memo = [[0] * (len(B) + 1) for _ in range(len(A) + 1)]
        for i in range(len(A) - 1, -1, -1):
            for j in range(len(B) - 1, -1, -1):
                if A[i] == B[j]:
                    memo[i][j] = memo[i + 1][j + 1] + 1
        return max(max(row) for row in memo)

    def findLength(self, A: List[int], B: List[int]) -> int:
        m,n=len(A),len(B)
        if m>n:
            A,B,m,n=B,A,n,m

        B_num_index={num:[] for i,num in enumerate(A)}
        for i,num in enumerate(B):
            if num in B_num_index:
                B_num_index[num].append(i)

        max_sub_str_len=0
        for i,num in enumerate(A):
            if num in B_num_index:
                for j in B_num_index[num]:
                    cur_len = self.find_max_comment_prefix(A[i:], B[j:])
                    if cur_len > max_sub_str_len:
                        max_sub_str_len = cur_len
                        if max_sub_str_len>=m-i:
                            return max_sub_str_len
        return max_sub_str_len

    def find_max_comment_prefix(self,A,B):
        min_len=min(len(A),len(B))
        left = 1
        right = min_len
        while left <= right:
            mid = (left + right) // 2
            if tuple(A[0:mid])==tuple(B[0:mid]):
                left = mid + 1
            else:
                right = mid - 1
        return (left + right) // 2
solution=Solution()

